<script>

export default {
  data() {
    return {
      translateVal: 0,
      successMessage: null,
      messageColor: "#40db80",
      formData: {},


    }
  },
  metaInfo: {
    title: "BENJI'S OFFICIAL WEBSITE"
  },
  methods: { 
    handleScroll (event) {
      this.translateVal = window.scrollY; 
    },
    encode(data) {
    return Object.keys(data)
      .map(key => encodeURIComponent(key) + '=' + encodeURIComponent(data[key]))
      .join('&')
    },
    handleSubmit(e) {
      if (this.formData.name && this.formData.email && this.formData.message){
        fetch('/', {
          method: 'POST',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          body: this.encode({
            'form-name': e.target.getAttribute('name'),
            ...this.formData,
          }),
        })
        .then(() => this.successMessage = 'Thank you for contacting Benji!')
        .catch( (error) => { this.successMessage = error;
                this.messageColor = "#db4040" } )
      } else {
        this.successMessage = 'Please fill out all of the form fields.';
        this.messageColor = "#db4040"; 
      }

    }
  },
  mounted () {
    window.addEventListener('scroll', this.handleScroll);
    window.instgrm.Embeds.process()
  },
}
